 = $passage_ligne."--".$boundary.$passage_ligne;//=====Ajout du message au format texte.$message.= "Content-Type: text/plain; charset=\"UTF-8\"".$passage_ligne;$message.= "Content-Transfer-Encoding: 8bit".$passage_ligne;$message.= $passage_ligne.$message_txt.$passage_ligne;//==========$message.= $passage_ligne."--".$boundary.$passage_ligne;//=====Ajout du message au format HTML$message.= "Content-Type: text/html; charset=\"UTF-8\"".$passage_ligne;$message.= "Content-Transfer-Encoding: 8bit".$passage_ligne;$message.= $passage_ligne.$message_html.$passage_ligne;//==========$message.= $passage_ligne."--".$boundary."--".$passage_ligne;$message.= $passage_ligne."--".$boundary."--".$passage_ligne;//========== //=====Envoi de l'e-mail.mail($mail,$sujet,$message,$header);//==========echo '<b class="bad">Message envoyer, redirection vers la page d\'accueil...</b>';echo('<META HTTP-EQUIV="Refresh" CONTENT="3; URL=http://weexa.mtxserv.fr/">');}?><h1>Partenaire</h1><section><h2>La Weexa Team pr&eacute;sente ses partenaires</h2><ul>	<hr/>	<li>		<a href="http://mozilla.org/fr"><img src="images/firefox.png"/></a>	</li>	<hr/>	<li>		<a href="http://extremcraft.net"><img src="images/logo extremcraft.png"/></a>	</li>	<hr/></section></br></br><h1>Reseaux sociaux</h1><section><h2>La Weexa Team est sur les reseaux sociaux suivez notre activit&eacute;;e !</h2><ul>	<hr/>	<li>		<a href="https://www.facebook.com/TheWeexaTeam?filter=3"><img style="width: 150px;" src="images/logo-facebook.png"/></a>	</li>	<hr/>	<li>		<a href="http://twitter.com/TheWeexaTeam"><img style="width: 150px;" src="images/logo-twitter.png"/></a>	</li>	<hr/>	<li>		<a href="http://www.youtube.com/user/remicaillot"><img style="width: 150px;" src="images/logo-youtube.png"/></a>	</li>	<hr/></section></br></br><h1>Licence et information</h1><a rel="license" href="http://creativecommons.org/licenses/by-nc-sa/3.0/fr/"><img alt="Licence Creative Commons" style="borde